function writeCard(a,b){$(".display").prepend('<div class="card"></div>');var c=$(".display .card").first();c.append('<div class="name">'+a[b].name+"</div>"),c.append('<div class="time" data-timestamp="'+a[b].timestamp+'">'+moment(a[b].timestamp).fromNow(!0)+"</div>"),c.append('<div class="message">'+a[b].message+"</div>"),c.append('<button data-id="'+a[b]._id+'" class="remove-card">Remove</button>')}$(document).ready(function(){$(".display").on("click",".remove-card",function(){var a=$(this);$.ajax({type:"DELETE",url:"/messenger/"+a.data("id"),success:function(){console.log("Deleting"),a.parent().remove()},error:function(a,b){alert("Error: ",b)},complete:function(){console.log("Delete Complete")}})})});